import React, { useState } from 'react';

const EligibilityForm = () => {
  const [user, setUser] = useState({ age: '', department: '', income: '', spend: '' });
  const [result, setResult] = useState(null);

  const handleChange = (e) => setUser({ ...user, [e.target.name]: e.target.value });
  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:3000/checkEligibility', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(user),
    });
    const data = await response.json();
    setResult(data.eligibility);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input name="age" value={user.age} onChange={handleChange} placeholder="Age" />
        <input name="department" value={user.department} onChange={handleChange} placeholder="Department" />
        <input name="income" value={user.income} onChange={handleChange} placeholder="Income" />
        <input name="spend" value={user.spend} onChange={handleChange} placeholder="Spend" />
        <button type="submit">Check Eligibility</button>
      </form>
      {result && <div>Eligibility: {result}</div>}
    </div>
  );
};

export default EligibilityForm;
